import * as d3 from "d3";

const getColor = (val) => {
  if (val >= 7) {
    return "green";
  } else if (6 <= val && val < 7) {
    return "yellow";
  } else if (5 <= val && val < 6) {
    return "orange";
  } else {
    return "red";
  }
};

export function PcpChart({ dataPcp, dimensions, selectedYear }) {
  d3.select("#svg_3").html("");
  const currentData = dataPcp.find((el) => el.year === selectedYear);
  const color = getColor(currentData.happiness_score);

  var margin = { top: 90, right: 80, bottom: 35, left: 80 },
    width = 1200 - margin.left - margin.right,
    height = 600 - margin.top - margin.bottom;

  var x = d3.scalePoint().range([0, width], 1),
    y = {},
    dragging = {};

  var line = d3.line(),
    axis = d3.axisLeft(),
    background,
    foreground;

  var svg = d3
    .select("#svg_3")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
    .append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

  x.domain(
    (dimensions = d3.keys(dataPcp[0]).filter(function (d) {
      if (d === "clusters" || d === "year") return false;
      return true;
    }))
  );

  dimensions.forEach(function (d) {
    if (d === "clusters" || d === "year") return;
    y[d] = d3
      .scaleLinear()
      .domain(
        d3.extent(dataPcp, function (p) {
          return +p[d];
        })
      )
      .range([height, 0]);
  });

  var g = svg
    .selectAll(".dimension")
    .data(dimensions)
    .enter()
    .append("g")
    .attr("class", "dimension")
    .attr("transform", function (d) {
      return "translate(" + x(d) + ")";
    });

  g.append("g")
    .attr("class", "axis")
    .each(function (d) {
      d3.select(this).call(axis.scale(y[d]));
    });
  g.append("text")
    .style("text-anchor", "middle")
    .attr("x", 15)
    .attr("y", -15)
    .attr("transform", "rotate(-25)")
    .text(function (d) {
      return d;
    });
}
