import React, { useState } from "react";

import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import AppBar from "@mui/material/AppBar";

import WorldMap from "../components/world-map";
import PCPPlot from "../components/pcp-plot";
import BarChart from "../components/bar-chart";
import RadarChart from "../components/radar-chart";
import { MenuItem, OutlinedInput, Select } from "@mui/material";
import SelectedDescription from "../components/selected-description";
import LineGraph from "../components/line-graph";
import DonutChart from "../components/donut-chart";

const Home = () => {
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedYear, setSelectedYear] = useState(2023);
  const [dataTable, setDataTable] = useState([]);

  // const changeIdiValue = (value) => {
  //   setIdi(value);
  // };

  const handleYearChange = (event) => {
    setSelectedYear(parseInt(event.target.value));
    setSelectedCountry("");
  };
  const handleCountryChange = (val) => {
    setSelectedCountry(val);
  };

  return (
    <Box>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="sticky" style={{ backgroundColor: "gray" }}>
          <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
            <Typography variant="h5">Visualisation- 564</Typography>
            <Typography variant="h5" textAlign="center" flex="1">
              Measuring Joy: Exploring the Link Between Sustainability and
              Happiness
            </Typography>
          </Toolbar>
        </AppBar>
      </Box>
      <div style={{ padding: "20px" }}>
        {/* <Select
        labelId="select-year-label"
        value={selectedYear}
        onChange={handleYearChange}
        label="Select Year"
        input={<OutlinedInput />}
        sx={{ marginLeft: "10px" }}
      >
        {[...Array(2023 - 2016 + 1)]
          .map((_, index) => 2023 - index)
          .map((year) => (
            <MenuItem key={year} value={year.toString()}>
              {year}
            </MenuItem>
          ))}
      </Select> */}
        {selectedYear && (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "70% 30%", // 70% for the first column, 30% for the second column
              padding: 5,
              height: "90vh"
            }}
          >
            <div style={{ display: "grid", gridTemplateRows: "40% 60%" }}>
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(2, 1fr)"
                }}
              >
                {" "}
                <WorldMap
                  selectedCountry={selectedCountry}
                  selectedYear={selectedYear}
                  handleCountryChange={handleCountryChange}
                />
                <BarChart
                  selectedCountry={selectedCountry}
                  selectedYear={selectedYear}
                />
                {/* <DonutChart width={400} selectedYear={selectedYear} /> */}
              </div>
              <div>{selectedCountry !== "" && <></>}</div>
            </div>
            {selectedCountry !== "" && (
              <SelectedDescription selectedCountry={selectedCountry} />
            )}
          </div>
        )}
      </div>
    </Box>
  );
};
export default Home;
