import * as d3 from "d3";

const thresholds = {
  clean_water_sanitation_score: [80, 60, 40, 20],
  freedom_to_make_life_choices: [80, 60, 40, 20],
  gdp_per_capita: [80, 60, 40, 20],
  gender_equality_score: [80, 60, 40, 20],
  generosity: [80, 60, 40, 20],
  good_health_score: [80, 60, 40, 20],
  happiness_score: [6, 4, 3, 1],
  healthy_life_expectancy: [6, 4, 3, 1],
  no_poverty_score: [6, 4, 3, 1],
  perceptions_of_corruption: [6, 4, 3, 1],
  quality_education_score: [6, 4, 3, 1],
  social_support: [6, 4, 3, 1],
  sustainable_index_score: [6, 4, 3, 1],
  zero_hunger_score: [6, 4, 3, 1]
};
const categories = {
  clean_water_sanitation_score: ["Greater than 80", "60-80", "40-60", "20-40"],
  freedom_to_make_life_choices: ["Greater than 80", "60-80", "40-60", "20-40"],
  gdp_per_capita: ["Greater than 80", "60-80", "40-60", "20-40"],
  gender_equality_score: ["Greater than 80", "60-80", "40-60", "20-40"],
  generosity: ["Greater than 80", "60-80", "40-60", "20-40"],
  good_health_score: ["Greater than 80", "60-80", "40-60", "20-40"],
  happiness_score: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  healthy_life_expectancy: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  no_poverty_score: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  perceptions_of_corruption: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  quality_education_score: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  social_support: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  sustainable_index_score: ["Greater than 6", "4-6", "3-4", "Less than 3"],
  zero_hunger_score: ["Greater than 6", "4-6", "3-4", "Less than 3"]
};

export function drawBarChart({ data, selectedItem }) {
  // Get thresholds and categories for the selected item
  const selectedThresholds = thresholds[selectedItem];
  const selectedCategories = categories[selectedItem];

  // Count the number of data points falling into each category
  const categoryCounts = Array(selectedCategories.length).fill(0);
  data.forEach((d) => {
    const value = d[selectedItem];
    const categoryIndex = selectedThresholds.findIndex(
      (threshold) => value > threshold
    );
    categoryCounts[categoryIndex]++;
  });

  // Prepare data for the chart
  const chartData = selectedCategories.map((category, i) => {
    return {
      range: category,
      count: categoryCounts[i]
    };
  });
  console.log(chartData);

  // Define chart dimensions
  const w = 650;
  const h = 400;
  const padding = 60;

  // Remove previous SVG if exists
  d3.select("#bar_chart").selectAll("*").remove();

  // Create scales
  const xScale = d3
    .scaleLinear()
    .domain([0, d3.max(categoryCounts)])
    .range([padding, w - padding]);

  const yScale = d3
    .scaleBand()
    .domain(selectedCategories)
    .range([padding, h - padding])
    .padding(0.1);

  // Create SVG element
  const svg = d3.select("#bar_chart").attr("width", w).attr("height", h);

  // Create bars
  svg
    .selectAll("rect")
    .data(chartData)
    .enter()
    .append("rect")
    .attr("x", padding)
    .attr("y", (d) => yScale(d.range))
    .attr("width", (d) => xScale(d.count) - padding)
    .attr("height", yScale.bandwidth())
    .attr("fill", (d, i) => {
      // Set bar colors based on categories
      if (i === 0) return "green"; // First category
      else if (i === selectedCategories.length - 1)
        return "red"; // Last category
      else return "steelblue"; // Other categories
    })
    .on("mouseover", function () {
      d3.select(this).attr("fill", "purple"); // Change color on hover
    })
    .on("mouseout", function (d, i) {
      // Restore original colors on mouseout
      const originalColor =
        i === 0
          ? "green"
          : i === selectedCategories.length - 1
          ? "red"
          : "steelblue";
      d3.select(this).attr("fill", originalColor);
    });

  // Create y-axis
  svg
    .append("g")
    .attr("transform", "translate(" + padding + ",0)")
    .call(d3.axisLeft(yScale));

  // Create x-axis
  svg
    .append("g")
    .attr("transform", "translate(0," + (h - padding) + ")")
    .call(d3.axisBottom(xScale).ticks(10));
}
