import * as d3 from "d3";

export function drawBarChart({ data }) {
  const w = 650;
  const barSpacing = 65;
  const barThickness = 55;
  const vertPadding = 15;
  const h = barSpacing * data.length + vertPadding;

  const xScale = d3.scaleLinear().domain([0, 350]).range([0, w]);
  const yScale = d3.scaleLinear().domain([0, data.length]).range([h, 0]);

  const xAxis = d3.axisBottom().scale(xScale).ticks(10);
  const yAxis = d3.axisRight().scale(yScale).ticks(0);

  const svg = d3.select("#bar-chart").attr("width", w).attr("height", h);

  const group = svg.selectAll("g").data(data).enter().append("g");

  group
    .append("rect")
    .attr("y", (d, i) => i * barSpacing + vertPadding)
    .attr("height", (d) => h - yScale(barThickness / barSpacing))
    .attr("fill", (d, i) => setBarColors(d, i))
    .transition()
    .duration(1000)
    .attr("width", (d) => xScale(d.Budget));

  svg
    .append("g")
    .style("font", "16px arial")
    .attr("transform", "translate(0," + h + ")")
    .call(xAxis);

  svg.append("g").call(yAxis);

  group
    .append("text")
    .text((d) => d.Area)
    .attr("text-anchor", "start")
    .attr("x", (d) => xScale(d.Budget * 0.1) + 20)
    .attr("y", (d, i) => i * barSpacing + vertPadding + barSpacing / 2)
    .attr("font-family", "arial, sans-serif")
    .attr("font-size", "18px")
    .attr("fill", "#333")
    .attr("opacity", 0)
    .transition()
    .duration(2000)
    .attr("opacity", 1);

  group
    .on("mouseover", function (d) {
      d3.select(this)
        .select("rect")
        .transition()
        .duration(250)
        .style("fill", "#ff8a65");
    })
    .append("title")
    .text((d) => d.Budget + " million");

  group.on("mouseout", function (d, i) {
    d3.select(this)
      .select("rect")
      .transition()
      .duration(400)
      .style("fill", function (d) {
        return setBarColors(d);
      });
  });
}

function setBarColors(d, i) {
  const colors = [
    "#64b5f6",
    "#4dd0e1",
    "#4fc3f7",
    "#4db6ac",
    "#0283AF",
    "#7EBC89",
    "#00187B"
  ];
  return colors[i];
}
