import React, { useEffect, useState } from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { makeStyles, Paper } from "@mui/material";
import RadarChart from "./radar-chart";
import LineGraph from "./line-graph";

const SelectedDescription = ({ selectedCountry }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/country_details", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        country: selectedCountry
      })
    })
      .then((response) => response.json())
      .then((data) => {
        const output = JSON.parse(JSON.stringify(data));
        setData(output.data);
      });
  }, [selectedCountry]);
  const currentData = data.filter((el) => el.year === 2023);
  console.log(currentData);
  return (
    currentData.length > 0 && (
      <div
        style={{
          display: "grid",
          gridTemplateRows: "repeat(3, 1fr)"
        }}
      >
        <Card sx={{ maxWidth: 345 }}>
          <CardMedia
            component="img"
            alt="flag"
            height="150"
            image={currentData[0].URL}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {selectedCountry}
            </Typography>
            {/* <Typography variant="body2" color="text.secondary">
          Lizards are a widespread group of squamate reptiles, with over 6,000
          species, ranging across all continents except Antarctica
        </Typography> */}
          </CardContent>
        </Card>
        <RadarChart data={data} />
        <LineGraph data={data} />
      </div>
    )
  );
};

export default SelectedDescription;
