import React, { useEffect, useState } from "react";
import { drawBarChart } from "../utils/svg-barchart";
import { FormControl, InputLabel, Select, MenuItem } from "@mui/material";

const items = ["Item 1", "Item 2", "Item 3", "Item 4", "Item 5"];

function BarChart({ selectedCountry, selectedYear }) {
  const [selectedItem, setSelectedItem] = useState("");

  const handleChange = (event) => {
    setSelectedItem(event.target.value);
  };
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("http://127.0.0.1:5000/country_details", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        country: [],
        year: [2023]
      })
    })
      .then((response) => response.json())
      .then((data) => {
        const output = JSON.parse(JSON.stringify(data));
        setData(output.data);
        drawBarChart({ data: data });
      });
  }, [selectedCountry, selectedYear]);

  return (
    <div>
      <FormControl variant="outlined" style={{ minWidth: 200 }}>
        <InputLabel id="dropdown-label">Select an item</InputLabel>
        <Select
          labelId="dropdown-label"
          id="dropdown"
          value={selectedItem}
          onChange={handleChange}
          label="Select an item"
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          {items.map((item, index) => (
            <MenuItem key={index} value={item}>
              {item}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <svg id="bar_chart"></svg>
    </div>
  );
}

export default BarChart;
