import * as d3 from "d3";

function purifyString(str) {
  var regex = /\s*\[[^\]]*\]\s*/g;
  return str.replace(regex, "");
}

export function createWorldMap({
  selected_year,
  selectedCountry,
  handleCountryChange
}) {
  var svg = d3.select("#world_map"),
    width = +svg.attr("width"),
    height = +svg.attr("height");

  var div = d3
    .select("body")
    .append("div")
    .attr("class", "tooltip")
    .style("opacity", 0);

  // Increase map scale
  var scale = 100;

  // Define map projection
  var projection = d3
    .geoMercator()
    .scale(scale)
    .center([0, 20])
    .translate([width / 2, height / 2]);

  // Define path generator
  var path = d3.geoPath().projection(projection);

  // Define color scale with the desired ranges
  var colorScale = d3
    .scaleThreshold()
    .domain([1, 5, 7]) // Adjusted domain
    .range(["red", "orange", "yellow", "green"]);

  var happinessScoreMap;
  var countryCodes;

  d3.queue()
    .defer(d3.json, process.env.PUBLIC_URL + "/data/world_countries.json")
    .defer(d3.csv, process.env.PUBLIC_URL + "/data/country-codes.csv")
    .defer(
      d3.csv,
      process.env.PUBLIC_URL +
        "/data/sustainable-score-" +
        selected_year +
        ".csv"
    )
    .await(createMap);

  function createMap(error, topo, codes, score) {
    if (error) throw error;

    happinessScoreMap = new Map(
      score.map((d) => [d.country, +d.happiness_score])
    );

    countryCodes = new Map(codes.map((x) => [x.alpha3, x.country]));

    svg
      .selectAll("path")
      .data(topo.features)
      .enter()
      .append("path")
      .attr("d", path)
      .attr("fill", function (t) {
        var countryCode = t.id;
        var countryName = countryCodes.get(countryCode) || "";
        var happinessScore =
          happinessScoreMap.get(purifyString(countryName)) || 0;
        return colorScale(happinessScore);
      })
      .style("stroke-width", 2)
      .style("stroke", "white")
      .style("opacity", 0.8)
      .on("mouseover", mouseover)
      .on("mousemove", mousemove)
      .on("mouseleave", mouseleave)
      .on("click", mouseclick);

    // Add legend
    var legend = svg
      .selectAll(".legend")
      .data(colorScale.domain())
      .enter()
      .append("g")
      .attr("class", "legend")
      .attr("transform", function (d, i) {
        return "translate(-20," + (height - 400 - i * 20) + ")";
      });

    legend
      .append("rect")
      .attr("x", width - 18)
      .attr("width", 18)
      .attr("height", 18) // Adjusted height for better visibility
      .style("fill", function (d) {
        return colorScale(d);
      });

    legend
      .append("text")
      .attr("x", width - 24)
      .attr("y", 9) // Adjusted y position for better alignment
      .attr("dy", ".35em")
      .style("text-anchor", "end")
      .text(function (d) {
        if (d === Infinity) {
          return "> 7";
        } else {
          return d + " - " + (d + 1);
        }
      });

    // Add country labels
    svg
      .selectAll(".country-label")
      .data(topo.features)
      .enter()
      .append("text")
      .attr("class", "country-label")
      .attr("x", function (d) {
        return path.centroid(d)[0];
      })
      .attr("y", function (d) {
        return path.centroid(d)[1];
      })
      .attr("text-anchor", "middle")
      .attr("alignment-baseline", "middle")
      .style("font-size", "6px")
      .style("fill", "black")
      .text(function (d) {
        var countryCode = d.id;
        return countryCodes.get(countryCode) || "";
      });
  }

  var mouseover = function (d) {
    div.transition().duration(200).style("opacity", 0.9);
    div
      .html(d.properties.name)
      .style("left", d3.event.pageX + "px")
      .style("top", d3.event.pageY - 28 + "px");
  };

  var mousemove = function () {};

  var mouseleave = function () {
    div.transition().duration(500).style("opacity", 0);
  };

  var mouseclick = function (d) {
    var countryCode = d.id;
    var countryName = countryCodes.get(countryCode) || "";
    handleCountryChange(countryName);
  };

  svg.on("mouseout", function () {
    div.transition().duration(500).style("opacity", 0);
  });
}
