import React, { useEffect, useState } from "react";

import RadarChart from "./radar-chart";
import LineGraph from "./line-graph";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
const SelectedDescription = ({ selectedCountry }) => {
  const [data, setData] = useState([]);
  const [expanded, setExpanded] = useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  useEffect(() => {
    fetch("http://127.0.0.1:5000/country_details", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        country: [selectedCountry],
        year: []
      })
    })
      .then((response) => response.json())
      .then((data) => {
        const output = JSON.parse(JSON.stringify(data));
        setData(output.data);
      });
  }, [selectedCountry]);
  const currentData = data.filter((el) => el.year === 2023);
  return (
    currentData.length > 0 && (
      <div
        style={{
          display: "grid",
          gridTemplateRows: "repeat(3, 1fr)"
        }}
      >
        <Card sx={{ maxWidth: 345 }}>
          <CardMedia
            component="img"
            alt="flag"
            height="150"
            image={currentData[0].URL}
            style={{ width: "auto", maxWidth: "100%" }} // Add this style
          />
          <CardContent>
            <Accordion expanded={expanded} onChange={handleExpandClick}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography gutterBottom variant="h5" component="div">
                  {selectedCountry}
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <div>
                  {Object.entries(currentData[0]).map(([key, value]) => {
                    if (key !== "URL" && key !== "year" && key !== "country") {
                      return (
                        <Typography
                          variant="body1"
                          color="text.secondary"
                          key={key}
                        >
                          {`${key}: ${value}`}
                        </Typography>
                      );
                    }
                    return null;
                  })}
                </div>
              </AccordionDetails>
            </Accordion>
          </CardContent>
        </Card>
        {/* <RadarChart data={data} /> */}
        {/* <LineGraph data={data} /> */}
      </div>
    )
  );
};

export default SelectedDescription;
